mapper.select:
SELECT COUNT(*) as cnt
FROM qna
    
      WHERE
    
    
     
        qtitle LIKE #{word}
      
     
        qcontent LIKE #{word}
     
      
        qid LIKE #{word}

 SELECT *
FROM qna
    
      WHERE
    
    
     
        qtitle LIKE #{word}
      
     
        qcontent LIKE #{word}
     
      
        qid LIKE #{word}
           
    
        ORDER BY qno DESC
        LIMIT #{offset}, 10

mapper.update:
UPDATE qna
SET qcount=qcount+1
WHERE qno=#{qno}

UPDATE qna
SET qtitle=#{qtitle}, qcontent=#{qcontent}
WHERE qno=#{qno}
