mapper.select:
SELECT COUNT(*) as cnt
FROM schedule
    
      WHERE
    
    
     
        slabel LIKE #{word}
      
     
        stitle LIKE #{word}
     
      
        slabeldate LIKE #{word}

SELECT sno, slabeldate, slabel, stitle, sdate
FROM schedule

WHERE

              
                  
                        slabel LIKE #{word}
                  
                 
                       stitle LIKE #{word}
                  
                  
                       slabeldate LIKE #{word}
                        
              
 ORDER BY sno ASC
 LIMIT #{offset}, 10;

SELECT sno, slabeldate, slabel, stitle, content, sdate
FROM schedule
WHERE sno= #{sno}

mapper.update:
UPDATE schedule
SET slabeldate=#{slabeldate}, slabel=#{slabel}, stitle=#{stitle}, content=#{content}  
WHERE sno=#{sno}
