mapper.update:
  UPDATE blog
  SET ansnum = ansnum + 1
  WHERE blogcategoryno = #{blogcategoryno} AND grpno = #{grpno} AND ansnum> #{ansnum}

mapper.select:
SELECT blogno, blogcategoryno, title, content, good, file1, file2, size2, cnt, replycnt, rdate, r FROM( SELECT blogno, blogcategoryno, title, content, good, file1, file2, size2, cnt, replycnt, rdate, rownum as r FROM( SELECT blogno, blogcategoryno, title, content, good, file1, file2, size2, cnt, replycnt, rdate FROM blog WHERE blogcategoryno=#{blogcategoryno}
AND title LIKE '%' || #{word} || '%'
AND content LIKE '%' || #{word} || '%'
AND title LIKE '%' || #{word} || '%' OR content LIKE '%' || #{word} || '%'
ORDER BY blogno DESC ) ) WHERE r >=#{startNum} AND r <= #{endNum}

SELECT blogno, blogcategoryno, mno, title, content, good, file1, file2, size2, cnt, replycnt, rdate, grpno, indent, ansnum, r FROM( SELECT blogno, blogcategoryno, mno, title, content, good, file1, file2, size2, cnt, replycnt, rdate, grpno, indent, ansnum, rownum as r FROM( SELECT blogno, blogcategoryno, mno, title, content, good, file1, file2, size2, cnt, replycnt, rdate,grpno, indent, ansnum FROM blog WHERE blogcategoryno=#{blogcategoryno}
AND title LIKE '%' || #{word} || '%'
AND content LIKE '%' || #{word} || '%'
AND title LIKE '%' || #{word} || '%' OR content LIKE '%' || #{word} || '%'
ORDER BY grpno DESC, ansnum ASC ) ) WHERE r >=#{startNum} AND r <= #{endNum}

SELECT blogno, blogcategoryno, mno, title, content, good, file1, file2, size2, cnt, replycnt, rdate, grpno, indent, ansnum
FROM blog
WHERE blogno=#{blogno}

mapper.update:
UPDATE blog
SET title=#{title}, content=#{content}, file1=#{file1}, file2=#{file2}, size2=#{size2} 
WHERE blogno=#{blogno}
