mapper.select:
SELECT COUNT(*) as cnt
FROM faq


WHERE cateno=#{cateno}

    
     
        AND ftitle LIKE #{word}
      
     
        AND fcontent LIKE #{word}

SELECT fno, cateno, ftitle, fcontent, fcount, fdate, uno
FROM faq

WHERE cateno=#{cateno}

    
     
        AND ftitle LIKE #{word}
      
     
        AND fcontent LIKE #{word}
     
  
         
    

ORDER BY fno DESC
LIMIT #{offset},10

SELECT fno, cateno, uno, ftitle, fcontent, fcount, fdate, grpno
FROM faq
WHERE fno=#{fno}

mapper.update:
UPDATE faq
SET fcount=fcount+1
WHERE fno=#{fno}

UPDATE faq
SET ftitle=#{ftitle}, fcontent=#{fcontent}
WHERE fno=#{fno}

mapper.select:
SELECT COUNT(*) as cnt
   FROM faq
   WHERE cateno = #{cateno};
