mapper.select:
SELECT COUNT(*) as cnt
FROM trash
    
      WHERE
    
    
     
        tname LIKE #{word}
      
     
        markerTitle LIKE #{word}
     
      
        maxcapa LIKE #{word}

SELECT tno, uno, tname, nowcapa, lat, lng, markerTitle, mdate
FROM trash
WHERE uno = #{uno}
    
      
       AND tname LIKE #{word}
      
      
       AND markerTitle LIKE #{word}
      
      
       AND maxcapa LIKE #{word}
            
   
 ORDER BY tno DESC
 LIMIT #{offset}, 10;

SELECT tno, tname, maxcapa, nowcapa, lat, lng, markerTitle, mdate
FROM trash
WHERE tno= #{tno}

mapper.update:
UPDATE trash
SET tname=#{tname}, maxcapa=#{maxcapa}, lat=#{lat}, lng=#{lng}, markerTitle=#{markerTitle} 
WHERE tno=#{tno}

mapper.select:
SELECT COUNT(*) as cnt
FROM trash
WHERE tname=#{tname}
