mapper.select:
SELECT uno, id, act, uname, email, udate, confirm, passwd
FROM user1
WHERE uno = #{uno}

mapper.update:
UPDATE user1
SET passwd=#{passwd}, uname=#{uname}, email=#{email}
WHERE uno=#{uno}

mapper.select:
SELECT COUNT(uno) as cnt
FROM user1
WHERE uno=#{uno} AND passwd=#{passwd}

SELECT COUNT(uno) as cnt
FROM user1
WHERE id=#{id} AND passwd=#{passwd}

SELECT uno, id, act, uname, email, udate, confirm, passwd
FROM user1
WHERE id=#{id }

mapper.update:
